/**
 * BCC to SRT
 * @author Prk
 *
 * https://github.com/BiliPrk/bcc2srt
 */
function convertBCCToSRT(bccData){function formatTimeSRT(time){function pad(number,length){for(var str=String(number);str.length<length;)str="0"+str;return str}var hours=Math.floor(time/3600),minutes=Math.floor(time%3600/60),seconds=(time%60).toFixed(3).replace(".",",");return pad(hours,2)+":"+pad(minutes,2)+":"+pad(seconds,6)}for(var bccObject=JSON.parse(bccData),lines=[],i=0;i<bccObject.body.length;i++){var subtitle=bccObject.body[i],line=`${i+1}\n${formatTimeSRT(subtitle.from)} --\x3e ${formatTimeSRT(subtitle.to)}\n${subtitle.content}`;lines.push(line)}return lines.join("\n\n")}function convertSRTToBCC(srtData){function parseTimeSRT(timeString){var timeParts=timeString.split(":");return 3600*parseInt(timeParts[0])+60*parseInt(timeParts[1])+parseFloat(timeParts[2].replace(",","."))}for(var lines=srtData.trim().split("\n\n"),bccObject={font_size:.4,font_color:"#FFFFFF",background_alpha:.5,background_color:"#9C27B0",Stroke:"none",body:[]},i=0;i<lines.length;i++){var subtitle=lines[i].split("\n"),timeData=subtitle[1].split(" --\x3e "),subtitleObject={from:parseTimeSRT(timeData[0]),to:parseTimeSRT(timeData[1]),location:2,content:subtitle[2]};bccObject.body.push(subtitleObject)}return JSON.stringify(bccObject)}